import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import './App.css';
import abi from './utils/WavePortal.json';

import 'bootstrap/dist/css/bootstrap.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const App = () => {
  const [currentAccount, setCurrentAccount] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  /**
   * Create a varaible here that holds the contract address after you deploy!
   */

  const [allWaves, setAllWaves] = useState([]);

  const contractAddress = "0xC9Fa3201801957c9296aA00680A891d305C71A99";
  const contractABI = abi.abi;

  const getAllWaves = async () => {
    try {
      const { ethereum } = window;
      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const wavePortalContract = new ethers.Contract(contractAddress, contractABI, signer);

        const waves = await wavePortalContract.getAllWaves();


        let wavesCleaned = [];
        waves.forEach(wave => {
          wavesCleaned.push({
            address: wave.waver,
            timestamp: new Date(wave.timestamp * 1000),
            message: wave.message
          });
        });

        setAllWaves(wavesCleaned);
      } else {
        console.log("Ethereum object doesn't exist!")
      }
    } catch (error) {
      console.log(error);
    }
  }

  const checkIfWalletIsConnected = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        console.log("Make sure you have metamask!");
        return;
      } else {
        console.log("We have the ethereum object", ethereum);
      }

      const accounts = await ethereum.request({ method: 'eth_accounts' });
      console.log(accounts);

      if (accounts.length !== 0) {
        const account = accounts[0];
        console.log("Found an authorized account:", account);
        setCurrentAccount(account);
        getAllWaves();
      } else {
        console.log("No authorized account found")
      }
    } catch (error) {
      console.log(error);
    }
  }

  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("Get MetaMask!");
        return;
      }

      const accounts = await ethereum.request({ method: "eth_requestAccounts" });

      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]); 
    } catch (error) {
      console.log(error)
    }
  }

  const [waveMessage, setWaveMessage] = useState("");
  const handleSubmit = (event) => {
    event.preventDefault();
    // console.log(event.target[0].value);


  }
  var isTime = False;

  const wave = async () => {
    try {
      const { ethereum } = window;

      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const wavePortalContract = new ethers.Contract(contractAddress, contractABI, signer);

        if(wavePortalContract.isAvailable()){

            let count = await wavePortalContract.getTotalWaves();
            console.log("Retrieved total wave count...", count.toNumber());

            const waveTxn = await wavePortalContract.wave(waveMessage, { gasLimit: 300000 });
            console.log("Mining...", waveTxn.hash);
            setIsLoading(true);

            await waveTxn.wait();
            console.log("Mined -- ", waveTxn.hash);
            setIsLoading(false);


            count = await wavePortalContract.getTotalWaves();
            console.log("Retrieved total wave count...", count.toNumber());
            getAllWaves();
        }
        else {
          alert("Wait 1 minute");
        }
      } else {
        console.log("Ethereum object doesn't exist!");
      }
    } catch (error) {
      console.log(error)
    }
  }

  useEffect(() => {
    checkIfWalletIsConnected();
  }, [])

  return (
    <div className="mainContainer">
      <div className="dataContainer">
        <div className="header">
        <span>👋</span> Hey there!
        </div>

        <div className="bio">
          I am Parth Soni and I worked on AI and Web3 so that's pretty cool right? Connect your Ethereum wallet and wave at me!
        </div>

        <form onSubmit={handleSubmit}>
            <div className="mb-3 my-3">
                <input type="text" className="form-control" onChange={(e) => setWaveMessage(e.target.value)}/>
            </div>
            <button type="submit" className="waveButton btn btn-primary" onClick={wave}>
              Wave at Me
            </button>
        </form>

        <br/>

        {isLoading &&
          <div class="spinner-border" role="status">
            <span class="sr-only"></span>
          </div>
        }


        {!currentAccount && (
          <button className="waveButton" onClick={connectWallet}>
            Connect Wallet
          </button>
        )}

        {allWaves.map((wave, index) => {
          return (
            <div key={index} style={{ marginTop: "16px", padding: "8px",border:"1px solid black" }}>

              <div style={{ fontSize: "1.2rem", padding: "5px" }}>Message: {wave.message}</div>
              <div style={{ fontSize: "0.8rem",  padding: "5px"}}>Time: {wave.timestamp.toString().slice(0,25)}</div>
              <div style={{ fontSize: "0.8rem",  padding: "5px"}}>Address: {wave.address}</div>

            </div>)
        })}
      </div>
    </div>
  );
}

export default App
